import React from 'react';
import TableRow from './TableRow';
import './Design.css';

class Table extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      employees : [
        {id: 1, name: 'Madhushree'},
        {id: 2, name: 'Priyanka'},
        {id: 3, name: 'Vaibhavi'},
        {id: 4, name: 'Pavithra'},
        {id: 5, name: 'Gomathi'}
      ]
    }
  }

  handleUserInput(filterText) {
    this.setState({filterText: filterText});
  };

  render() {
    return (
      <table align="center">
        <thead>
          {this.renderTableHeader()}
        </thead>
        <tbody>
          {this.renderTableData()}
        </tbody>
      </table>
    )
  }

  renderTableHeader(){
    return (
      <tr>
        <th>ID</th>
        <th>NAME</th>
        <th>ACTION</th>
      </tr>
    )
  }

  renderTableData() {
    return (
      this.state.employees.map((employee) => {
        return (
          <TableRow id={employee.id} name={employee.name}></TableRow>       
        )
      })
    )
  }

    render() {
      var onemloyeesUpdate = this.props.onemployeesUpdate;
      var filterText = this.props.filterText;
      var employees = this.props.employees.map(function(employees) {
        if (employees.name.indexOf(filterText) === -1) {
          return;
        }
        });
      return (
        <div>


  <button type="button" onClick={this.props.onRowAdd} className="btn btn-success pull-right">Add</button>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
              </tr>
            </thead>

            <tbody>
              {employees}

            </tbody>

          </table>
        </div>
      );

    }



    render() {
      return (
        <td>
          <input type='text' name={this.props.cellData.type} id={this.props.cellData.id} value={this.props.cellData.value} onChange={this.props.onemployeesTableUpdate}/>
        </td>
      );

    }

}


export default Table;
