import React from 'react';
import TableRow from './TableRow';
import './Design.css';

class Table extends React.Component {


  handleUserInput(filterText) {
    this.setState({filterText: filterText});
  };

  handleAddEvent(evt) {
    var id = (+ new id() + new (name).toString(36));
    var product = {
      id: id,
      name: ""
    }
    this.state.employees.push(employees);
    this.setState(this.state.employees);
  }

  handleEmployeesTable(evt) {
    var employees = {
      id: evt.target.id,
      name: evt.target.name
    };

    var employees = this.state.employees.slice();
    var newemployees = employees.map(function(employees) {

      for (var key in employees) {
        if (key == employees.name && employees.id == employees.id) {
          employees[key] = employees.value;

        }
      }
      return employees;
    });
  }

  render() {
    return (
      <table align="center">
        <thead>
          {this.renderTableHeader()}
        </thead>
        <tbody>
          {this.renderTableData()}
        </tbody>
      </table>
    )
  }

  renderTableHeader(){
    return (
      <tr>
        <th>ID</th>
        <th>NAME</th>
        <th>ACTION</th>
      </tr>
    )
  }

  renderTableData() {
    return (
      this.state.employees.map((employees) => {
        return (
          <TableRow id={employees.id} name={employees.name}></TableRow>       
        )
      })
    )
  }

  render() {
    var onemloyeesUpdate = this.props.onemployeesUpdate;
    var filterText = this.props.filterText;

    var employees = this.props.employees.map(function(employees) {
      if (employees.name.indexOf(filterText) === -1) {
        return;
      }
    });

    return (
      <div>
        <button type="button" onClick={this.props.onRowAdd} className="btn btn-success pull-right">Add</button>
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
            </tr>
          </thead>
          <tbody>
            {employees}
          </tbody>
        </table>
      </div>
    );
  }

  render() {
    return (
      <td>
        <input type='text' name={this.props.cellData.type} id={this.props.cellData.id} value={this.props.cellData.value} onChange={this.props.onemployeesTableUpdate}/>
      </td>
      )
    }
}


export default Table;
